package jon.vs.Redd_pro_vardemo;


public class switch_via_string {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	  String string="Hi";
	  
	  switch (string) {
	case "Hi":System.out.println("Hi");
		
		
	case "Hello":System.out.println("Hello");
	case "Adios":System.out.println("Adios");
	case "Well":System.out.println("Well");break;
	case "Chi-chao":System.out.println("Chi-chao");
	
	default:System.out.println("Chal foot yha se");
		break;
	}
	  
	  		
	}
}
